class Evaluator {
    evaluate (argument = {criterion: 'precision'}) {
        var {criterion} = argument;
        var method = 'evaluateBy_' + criterion;
        return this[method](argument);
    }
    evaluateBy_precision(argument) {
        let {predicated, truth} = argument;
        var correctCount = predicated.reduce((pre, now, index) => {
            return pre + (now == truth[index] ? 1 : 0);
        }, 0);
        var totalCount = predicated.length;
        return correctCount / totalCount;
    }
};

module.exports = Evaluator;
