var ai = require('../main.js');

function testDataLoader () {
    console.log('test DataLoader');
    var data = ai.DataLoader.load({filePath: 'iris.csv', labelName: 'class'});
    console.log(data[0]);
    console.log(data.labels[0]);
    console.log(data['sepalwidth'][0]);

    data.shuffle();
    var [data1, data2] = data.split([9, 1]);
    console.log(data1.size);
    console.log(data2.size);
}

function testSimpleVote () {
    console.log('test SimpleVote');
    var data = ai.DataLoader.load({filePath: 'iris.csv', labelName: 'class'});
    var [trainData, testData] = data.split([9, 1]);
    var vote = new ai.Classifier.SimpleVote();
    vote.train({data: trainData});
    console.log('precision of SimpleVote is: ' + vote.evaluate({data: testData}));
}

testDataLoader();
testSimpleVote();
